Ext.define('GFManager.view.analyzer.activity.ActivityAnalysisEditViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.activityanalysisedit',

    getStatisticsAllActivity: function(period) {
        var me = this;

        var key = Ext.getStore('ManagerInfo').data.items[0].get('key');
        var token = Ext.getStore('ManagerInfo').data.items[0].get('token');

        Ext.Ajax.request({
            url: '/api/v1/analyzer/activities',
            method: 'GET',
            headers: {
                'x-key': key,
                'x-access-token': token
            },
            params:{
                period: period
            },
            success: function(response){
                var decodeResponse 		= Ext.decode(response.responseText);

                var activityAnalysisAllStore	= me.getStore('activityAnalysisAll');
                activityAnalysisAllStore.loadData(decodeResponse.data, false);

            },
            failure: function(response){console.log(response);}

        });
    },

    getStatisticActivity: function(id, period) {
        var me = this;

        var key = Ext.getStore('ManagerInfo').data.items[0].get('key');
        var token = Ext.getStore('ManagerInfo').data.items[0].get('token');

        Ext.Ajax.request({
            url: '/api/v1/analyzer/activities/' + id,
            method: 'GET',
            headers: {
                'x-key': key,
                'x-access-token': token
            },
            params:{
                period: period
            },
            success: function(response){
                var decodeResponse 		= Ext.decode(response.responseText);

                var activityAnalysisStore	= me.getStore('activityAnalysis');
                activityAnalysisStore.loadData(decodeResponse.data, false);

            },
            failure: function(response){console.log(response);}

        });
    },

    getStatisticsByPeriod: function(period) {
        var activity				= this.getViewModel().get('theActivity');
        var me 						= this;


        var key = Ext.getStore('ManagerInfo').data.items[0].get('key');
        var token = Ext.getStore('ManagerInfo').data.items[0].get('token');

        if(activity.create) {

            Ext.Ajax.request({
                url: '/api/v1/analyzer/activitiesbyperiod',
                method: 'GET',
                headers: {
                    'x-key': key,
                    'x-access-token': token
                },
                params:{
                    period: period
                },
                success: function(response){
                    var decodeResponse 		= Ext.decode(response.responseText);

                    var activityAnalysisPeriodStore	= me.getStore('activityAnalysisPeriod');
                    activityAnalysisPeriodStore.loadData(decodeResponse.data, false);

                },
                failure: function(response){console.log(response);}

            });

        } else {

            Ext.Ajax.request({
                url: '/api/v1/analyzer/activitiesbyperiod',
                method: 'GET',
                headers: {
                    'x-key': key,
                    'x-access-token': token
                },
                params:{
                    aid: activity.get('id'),
                    period: period
                },
                success: function(response){
                    var decodeResponse 		= Ext.decode(response.responseText);

                    var activityAnalysisPeriodStore	= me.getStore('activityAnalysisPeriod');
                    activityAnalysisPeriodStore.loadData(decodeResponse.data, false);

                },
                failure: function(response){console.log(response);}

            });
        }
    },

    onActivityTopPeriodRadiogroupChange: function(field, newValue, oldValue, eOpts) {
        var activity				= this.getViewModel().get('theActivity');
        var statisticsActivities	= this.lookupReference('statisticsActivities');
        var statisticsAllActivities	= this.lookupReference('statisticsAllActivities');

        if(activity.create) {
            this.getStatisticsAllActivity(newValue.period);

            statisticsActivities.hide();
            statisticsAllActivities.show();
        } else {
            this.getStatisticActivity(activity.get('id'), newValue.period);

            statisticsActivities.show();
            statisticsAllActivities.hide();
        }
    },

    onActivityBottomPeriodRadiogroupChange: function(field, newValue, oldValue, eOpts) {
        this.getStatisticsByPeriod(newValue.period);
    },

    onActivityAnalysisFormShow: function(component, eOpts) {
        var activity				= this.getViewModel().get('theActivity');
        var statisticsActivities	= this.lookupReference('statisticsActivities');
        var statisticsAllActivities	= this.lookupReference('statisticsAllActivities');

        var nStart = new Date().getTime();
        console.log('게임 메카닉 결과 분석 시작 시점(Activity)');


        if(activity.create) {
            this.getStatisticsAllActivity(1);

            statisticsActivities.hide();
            statisticsAllActivities.show();
        } else {
            this.getStatisticActivity(activity.get('id'),1);

            statisticsActivities.show();
            statisticsAllActivities.hide();
        }

        this.getStatisticsByPeriod(1);


        var nEnd =  new Date().getTime();
        console.log('게임 메카닉 결과 분석 종료 시점(Activity)');

        var nDiff = nEnd - nStart;

        console.log('게임 메카닉 결과 분석 기능 반응 시간(Activity) : '+ nDiff + ' (ms)');


    }

});
