Ext.define('GFManager.view.analyzer.permission.PermissionAnalysisEditViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.permissionanalysisedit',

    getStatisticsAllPermissions: function() {
        var me = this;

        var key = Ext.getStore('ManagerInfo').data.items[0].get('key');
        var token = Ext.getStore('ManagerInfo').data.items[0].get('token');

        Ext.Ajax.request({
            url: '/api/v1/analyzer/permissions',
            method: 'GET',

            headers: {
                'x-key': key,
                'x-access-token': token
            },
            success: function(response){
                var decodeResponse 		= Ext.decode(response.responseText);

                var permissionAnalysisAllStore	= me.getStore('permissionAnalysisAll');
                permissionAnalysisAllStore.loadData(decodeResponse.data, false);

            },
            failure: function(response){console.log(response);}

        });
    },

    getStatisticsPermission: function(permissionId) {
        var me = this;

        var key = Ext.getStore('ManagerInfo').data.items[0].get('key');
        var token = Ext.getStore('ManagerInfo').data.items[0].get('token');

        Ext.Ajax.request({
            url: '/api/v1/analyzer/permissions/'+permissionId,
            method: 'GET',

            headers: {
                'x-key': key,
                'x-access-token': token
            },
            success: function(response){
                var decodeResponse 		= Ext.decode(response.responseText);

                var permissionAnalysisStore	= me.getStore('permissionAnalysis');
                permissionAnalysisStore.loadData(decodeResponse.data, false);

            },
            failure: function(response){console.log(response);}

        });
    },

    getStatisticsPermissionByPeriod: function(period) {
        var permission	= this.getViewModel().get('thePermission');
        var me 			= this;

        var key = Ext.getStore('ManagerInfo').data.items[0].get('key');
        var token = Ext.getStore('ManagerInfo').data.items[0].get('token');

        if(permission.create) {

            Ext.Ajax.request({
                url: '/api/v1/analyzer/permissionsbyperiod',
                method: 'GET',

                headers: {
                    'x-key': key,
                    'x-access-token': token
                },
                params:{
                    period: period
                },
                success: function(response){
                    var decodeResponse 		= Ext.decode(response.responseText);

                    var permissionAnalysisPeriodStore	= me.getStore('permissionAnalysisPeriod');
                    permissionAnalysisPeriodStore.loadData(decodeResponse.data, false);

                },
                failure: function(response){console.log(response);}

            });

        } else {

            Ext.Ajax.request({
                url: '/api/v1/analyzer/permissionsbyperiod',
                method: 'GET',

                headers: {
                    'x-key': key,
                    'x-access-token': token
                },
                params:{
                    id: permission.get('id'),
                    period: period
                },
                success: function(response){
                    var decodeResponse 		= Ext.decode(response.responseText);

                    var permissionAnalysisPeriodStore	= me.getStore('permissionAnalysisPeriod');
                    permissionAnalysisPeriodStore.loadData(decodeResponse.data, false);

                },
                failure: function(response){console.log(response);}

            });
        }
    },

    onPermissionPeriodRadiogroupChange: function(field, newValue, oldValue, eOpts) {
        this. getStatisticsPermissionByPeriod(newValue.period);
    },

    onPermissionAnalysisFormShow: function(component, eOpts) {
        var permission				= this.getViewModel().get('thePermission');
        var statisticsAllPermissions= this.lookupReference('statisticsAllPermissions');
        var statisticsPermission	= this.lookupReference('statisticsPermission');


        var nStart = new Date().getTime();
        console.log('게임 메카닉 결과 분석 시작 시점(Permission)');


        if(permission.create) {
            this.getStatisticsAllPermissions();
            statisticsAllPermissions.show();
            statisticsPermission.hide();
        } else {
            this.getStatisticsPermission(permission.get('id'));
            statisticsAllPermissions.hide();
            statisticsPermission.show();
        }

        this.getStatisticsPermissionByPeriod(1);



        var nEnd =  new Date().getTime();
        console.log('게임 메카닉 결과 분석 종료 시점(Permission)');

        var nDiff = nEnd - nStart;

        console.log('게임 메카닉 결과 분석 기능 반응 시간(Permission) : '+ nDiff + ' (ms)');
    }

});
