Ext.define('GFManager.view.manager.activity.ActivityListViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.activitylist',

    viewActivity: function(record) {
        this.fireViewEvent('viewActivity', this.getView(), record);
    },

    onActivityAddButtonClick: function(button, e, eOpts) {
        this.viewActivity(null);
    },

    onActivityDelButtonClick: function(button, e, eOpts) {
        if(Ext.getStore('ManagerInfo').data.length !== 0) {
            var ActivityGrid = button.up('activitylist');
            var selection = ActivityGrid.getSelectionModel().getSelection()[0];

            selection.erase();

            this.viewActivity(selection);

            button.disable();

        } else {
            Ext.MessageBox.alert('로그인 요청', '로그인 후 이용이 가능합니다.', function(btn){
                if (btn == 'ok') {
                }
            }, this);

        }
    },

    onActivityListviewSelectionChange: function(model, selected, eOpts) {
        var delBtn = this.lookupReference('del_activity_btn');
        delBtn.enable();
    },

    onActivityListGridpanelItemDblClick: function(dataview, record, item, index, e, eOpts) {
        this.viewActivity(record);
    }

});
