Ext.define('GFManager.view.manager.badge.BadgeListViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.badgelist',

    viewBadge: function(record) {
        this.fireViewEvent('viewBadge', this.getView(), record);
    },

    onBadgeAddButtonClick: function(button, e, eOpts) {
        this.viewBadge(null);
    },

    onBadgeDelButtonClick: function(button, e, eOpts) {
        if(Ext.getStore('ManagerInfo').data.length !== 0) {
            var badgeGrid = button.up('badgelist');
            var selection = badgeGrid.getSelectionModel().getSelection()[0];

            selection.erase();

            this.viewBadge(selection);

            button.disable();

        } else {
            Ext.MessageBox.alert('로그인 요청', '로그인 후 이용이 가능합니다.', function(btn){
                if (btn == 'ok') {
                }
            }, this);

        }
    },

    onBadgelistviewSelectionChange: function(model, selected, eOpts) {
        var delBtn = this.lookupReference('del_badge_btn');
        delBtn.enable();
    },

    onBadgeGridpanelItemDblClick: function(dataview, record, item, index, e, eOpts) {
        this.viewBadge(record);
    }

});
