Ext.define('GFManager.view.manager.level.LevelListViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.levellist',

    viewLevel: function(record) {
        this.fireViewEvent('viewLevel', this.getView(), record);
    },

    onLevelAddButtonClick: function(button, e, eOpts) {
        this.viewLevel(null);
    },

    onLevelDelButtonClick: function(button, e, eOpts) {
        if(Ext.getStore('ManagerInfo').data.length !== 0) {
            var LevelGrid = button.up('levellist');
            var selection = LevelGrid.getSelectionModel().getSelection()[0];

            selection.erase();

            this.viewLevel(selection);

            button.disable();

        } else {
            Ext.MessageBox.alert('로그인 요청', '로그인 후 이용이 가능합니다.', function(btn){
                if (btn == 'ok') {
                }
            }, this);

        }
    },

    onLevelGridpanelItemDblClick: function(dataview, record, item, index, e, eOpts) {
        this.viewLevel(record);
    },

    onLevelGridpanelSelectionChange: function(model, selected, eOpts) {
        var delLevelBtn = this.lookupReference('del_level_btn');
        delLevelBtn.enable();
    }

});
