Ext.define('GFManager.view.manager.status.StatusListViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.statuslist',

    viewStatus: function(record) {
        this.fireViewEvent('viewStatus', this.getView(), record);
    },

    onStatusAddButtonClick: function(button, e, eOpts) {
        this.viewStatus(null);
    },

    onStatusDelButtonClick: function(button, e, eOpts) {
        if(Ext.getStore('ManagerInfo').data.length !== 0) {
            var StatusGrid = button.up('statuslist');
            var selection = StatusGrid.getSelectionModel().getSelection()[0];
            selection.erase();

            this.viewStatus(selection);

            button.disable();
        } else {
            Ext.MessageBox.alert('로그인 요청', '로그인 후 이용이 가능합니다.', function(btn){
                if (btn == 'ok') {
                }
            }, this);

        }
    },

    onStatusGridpanelStatusItemDblClick: function(dataview, record, item, index, e, eOpts) {
        this.viewStatus(record);
    },

    onStatuslistviewSelectionChange: function(model, selected, eOpts) {
        var delStatusBtn = this.lookupReference('del_status_btn');
        delStatusBtn.enable();
    }

});
